// src/components/Admin.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState([]);
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const response = await axios.get('http://localhost:5000/admin/users');
      setUsers(response.data);
    };
    fetchUsers();
  }, []);

  const selectUser = async (userId) => {
    setSelectedUser(userId);
    const postsResponse = await axios.get(`http://localhost:5000/admin/users/${userId}/posts`);
    const commentsResponse = await axios.get(`http://localhost:5000/admin/users/${userId}/comments`);
    const photosResponse = await axios.get(`http://localhost:5000/admin/users/${userId}/photos`);

    setPosts(postsResponse.data);
    setComments(commentsResponse.data);
    setPhotos(photosResponse.data);
  };

  const deleteUser = async (userId) => {
    await axios.delete(`http://localhost:5000/admin/users/${userId}`);
    setUsers(users.filter(user => user.id !== userId));
    setSelectedUser(null);
    setPosts([]);
    setComments([]);
    setPhotos([]);
  };

  return (
    <div>
      <h1>Admin Panel</h1>
      <ul>
        {users.map((user) => (
          <li key={user.id} onClick={() => selectUser(user.id)}>
            {user.username}
          </li>
        ))}
      </ul>
      {selectedUser && (
        <div>
          <h2>User Details</h2>
          <h3>Posts</h3>
          {posts.map((post) => (
            <div key={post.id}>{post.content}</div>
          ))}
          <h3>Comments</h3>
          {comments.map((comment) => (
            <div key={comment.id}>{comment.text}</div>
          ))}
          <h3>Photos</h3>
          {photos.map((photo) => (
            <img key={photo.id} src={photo.url} alt="User's uploaded" />
          ))}
          <button onClick={() => deleteUser(selectedUser)}>Delete User</button>
        </div>
      )}
    </div>
  );
};

export default AdminPage;
