// src/pages/PostPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Comment from '../components/Comment';

const PostPage = () => {
  const [posts, setPosts] = useState([]);
  const [newPostContent, setNewPostContent] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/posts');
        setPosts(response.data);
      } catch (error) {
        setError('Error fetching posts');
        console.error('Error fetching posts:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchPosts();
  }, []); // Only run once on component mount

  const handlePostSubmit = async (e) => {
    e.preventDefault();
    if (newPostContent.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/posts', { content: newPostContent });
        setPosts([...posts, response.data]);
        setNewPostContent('');
      } catch (error) {
        setError('Error posting new content');
        console.error('Error posting new content:', error);
      }
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>Posts</h1>
      <form onSubmit={handlePostSubmit}>
        <textarea
          value={newPostContent}
          onChange={(e) => setNewPostContent(e.target.value)}
          placeholder="Write a new post"
          required
        />
        <button type="submit">Add Post</button>
      </form>
      <div>
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post.id} style={{ border: '1px solid #ddd', margin: '10px 0', padding: '10px' }}>
              <h3>{post.content}</h3>
              <Comment postId={post.id} />
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>
    </div>
  );
};

export default PostPage;
