// src/pages/GalleryPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GalleryPage = () => {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const fetchPhotos = async () => {
      try {
        const response = await axios.get('http://localhost:5000/user/photos'); // Kendi fotoğraflarınızı çeken API endpoint'i
        setPhotos(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching photos:', error);
        setLoading(false);
      }
    };
    fetchPhotos();
  }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleFileUpload = async (e) => {
    e.preventDefault();
    if (selectedFile) {
      const formData = new FormData();
      formData.append('photo', selectedFile);

      try {
        const response = await axios.post('http://localhost:5000/user/photos', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        setPhotos([...photos, response.data]);
        setSelectedFile(null);
      } catch (error) {
        console.error('Error uploading photo:', error);
      }
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>My Gallery</h1>
      <form onSubmit={handleFileUpload}>
        <input type="file" onChange={handleFileChange} required />
        <button type="submit">Upload Photo</button>
      </form>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {photos.length > 0 ? (
          photos.map((photo) => (
            <div key={photo.id} style={{ width: '200px', height: '200px' }}>
              <img src={photo.url} alt="User upload" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
            </div>
          ))
        ) : (
          <p>No photos available</p>
        )}
      </div>
    </div>
  );
};

export default GalleryPage;
