// src/components/AdminPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState([]);
  const [photos, setPhotos] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      const response = await axios.get('http://localhost:5000/admin/users');
      setUsers(response.data);
    };
    fetchUsers();
  }, []);

  const selectUser = async (userId) => {
    setSelectedUser(userId);
    const postsResponse = await axios.get(`http://localhost:5000/admin/users/${userId}/posts`);
    const commentsResponse = await axios.get(`http://localhost:5000/admin/users/${userId}/comments`);
    const photosResponse = await axios.get(`http://localhost:5000/admin/users/${userId}/photos`);

    setPosts(postsResponse.data);
    setComments(commentsResponse.data);
    setPhotos(photosResponse.data);
  };

  const deleteUser = async (userId) => {
    await axios.delete(`http://localhost:5000/admin/users/${userId}`);
    setUsers(users.filter(user => user.id !== userId));
    setSelectedUser(null);
    setPosts([]);
    setComments([]);
    setPhotos([]);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-4xl font-bold mb-8">Admin Panel</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        <ul className="bg-white p-6 rounded shadow-md overflow-auto max-h-96">
          {users.map((user) => (
            <li
              key={user.id}
              onClick={() => selectUser(user.id)}
              className="cursor-pointer p-2 border-b hover:bg-gray-200"
            >
              {user.username}
            </li>
          ))}
        </ul>
        {selectedUser && (
          <div className="bg-white p-6 rounded shadow-md">
            <h2 className="text-2xl mb-4">User Details</h2>
            <div className="mb-4">
              <h3 className="text-xl font-semibold">Posts</h3>
              {posts.length > 0 ? (
                posts.map((post) => (
                  <div key={post.id} className="border p-2 my-2">
                    {post.content}
                  </div>
                ))
              ) : (
                <p>No posts available</p>
              )}
            </div>
            <div className="mb-4">
              <h3 className="text-xl font-semibold">Comments</h3>
              {comments.length > 0 ? (
                comments.map((comment) => (
                  <div key={comment.id} className="border p-2 my-2">
                    {comment.text}
                  </div>
                ))
              ) : (
                <p>No comments available</p>
              )}
            </div>
            <div className="mb-4">
              <h3 className="text-xl font-semibold">Photos</h3>
              {photos.length > 0 ? (
                photos.map((photo) => (
                  <img key={photo.id} src={photo.url} alt="User's uploaded" className="w-full my-2 rounded" />
                ))
              ) : (
                <p>No photos available</p>
              )}
            </div>
            <button
              onClick={() => deleteUser(selectedUser)}
              className="bg-red-500 text-white p-2 rounded mt-4"
            >
              Delete User
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPage;
