// src/pages/GalleryPage.js
import React, { useState, useEffect } from 'react';

const fakePhotos = [
  { id: 1, url: 'https://via.placeholder.com/200' },
  { id: 2, url: 'https://via.placeholder.com/200' },
  { id: 3, url: 'https://via.placeholder.com/200' }
];

const GalleryPage = () => {
  const [photos, setPhotos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    // API çağrısı yerine sahte verileri kullanma
    setTimeout(() => {
      setPhotos(fakePhotos);
      setLoading(false);
    }, 1000);
  }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleFileUpload = (e) => {
    e.preventDefault();
    if (selectedFile) {
      const newPhoto = { id: photos.length + 1, url: URL.createObjectURL(selectedFile) };
      setPhotos([...photos, newPhoto]);
      setSelectedFile(null);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-4xl font-bold mb-8">My Gallery</h1>
      <form onSubmit={handleFileUpload} className="mb-8">
        <input type="file" onChange={handleFileChange} required />
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Upload Photo</button>
      </form>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
        {photos.length > 0 ? (
          photos.map((photo) => (
            <div key={photo.id} style={{ width: '200px', height: '200px' }}>
              <img src={photo.url} alt="User upload" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
            </div>
          ))
        ) : (
          <p className="text-gray-600">No photos available</p>
        )}
      </div>
    </div>
  );
};

export default GalleryPage;
